import React from 'react';
import propTypes from 'prop-types';
import styled from 'styled-components';

const HalfImage = styled.img`
	width: 50%;
	/* display: inline; */
`;
const UpperImage = styled.img`
	width: 50%;
	height: 50%;
`;
const LowerImage = styled.img`
	width: 50%;
	height: 50%;
`;
const ImageContainer = styled.div`
	display: flex;
`;
const LeftImageContainer = styled.div`
	flex-basis: 1;
`;
const RightImageContainer = styled.div`
	flex-basis: 1;
	display: flex;
	flex-direction: column;
`;
const PostImages = ({ images }) => {
	if (images.length === 1) {
		return (
			<>
				<img src="" alt="" />
			</>
		);
	}
	if (images.length === 2) {
		return (
			<>
				<HalfImage src={images[0].src}></HalfImage>
				<HalfImage src={images[1].src}></HalfImage>
			</>
		);
	}

	return (
		<>
			<ImageContainer>
				<HalfImage src={images[0].src} alt="" />
				{/* <ImageContainer> */}
				<UpperImage src={images[1].src}></UpperImage>
				<LowerImage src={images[2].src}></LowerImage>
				{/* </ImageContainer> */}
			</ImageContainer>
		</>
	);
};

PostImages.propTypes = {
	images: propTypes.array,
};

export default PostImages;
