import {
	put,
	delay,
	takeEvery,
	takeLatest,
	all,
	fork,
	call,
} from 'redux-saga/effects';
import {
	ADD_POST_REQUEST,
	ADD_POST_SUCCESS,
	ADD_POST_FAILURE,
	ADD_COMMENT_REQUEST,
	ADD_COMMENT_SUCCESS,
	ADD_COMMENT_FAILURE,
	ADD_POST_TO_ME,
	REMOVE_POST_REQUEST,
	REMOVE_POST_SUCCESS,
	REMOVE_POST_FAILURE,
	REMOVE_POST_OF_MINE,
	LOAD_POST_REQUEST,
	LOAD_POST_FAILURE,
	LOAD_POST_SUCCESS,
	LIKE_POST_REQUEST,
	UNLIKE_POST_REQUEST,
	LIKE_POST_FAILURE,
	UNLIKE_POST_FAILURE,
	LIKE_POST_SUCCESS,
	UNLIKE_POST_SUCCESS,
	UPLOAD_IMAGES_REQUEST,
	UPLOAD_IMAGES_FAILURE,
	UPLOAD_IMAGES_SUCCESS,
} from '../actions/index';
// import shortid from 'shortid';
// import { generateDummyPost } from '../reducer/post';
import {
	uploadPost,
	uploadComment,
	fetchPost,
	likePostAPI,
	unlikePostAPI,
	removePostAPI,
} from '../API/index';
function* addPost(action) {
	// console.log('saga add post');
	try {
		// const result = yield call();
		// yield delay(1000);
		// const id = shortid.generate();
		const result = yield call(uploadPost, action.data);
		// result {content, PostId, UserId}

		yield put({
			type: ADD_POST_SUCCESS,
			data: result.data,
			// data: {
			// 	id,
			// 	content: action.data,
			// },
		});
		yield put({
			type: ADD_POST_TO_ME,
			data: result.data.id,
			// data: id,
		});
	} catch (error) {
		yield put({
			type: ADD_POST_FAILURE,
			error: error.response.message,
		});
	}
}
function* addComment(action) {
	try {
		const result = yield call(uploadComment, action.data);
		// yield delay(1000);
		// result = {content, PostId, UserId}
		yield put({
			type: ADD_COMMENT_SUCCESS,
			data: result.data,
			// data: action.data,
		});
	} catch (err) {
		console.error(err);
		yield put({
			type: ADD_COMMENT_FAILURE,
			error: err.response.message,
		});
	}
}

function* removePost(action) {
	try {
		// yield delay(1000);
		const result = yield call(removePostAPI, action.data);
		yield put({
			type: REMOVE_POST_SUCCESS,
			data: result.data,
		});
		yield put({
			type: REMOVE_POST_OF_MINE,
			data: action.data,
		});
	} catch (error) {
		yield put({
			type: REMOVE_POST_FAILURE,
			data: error,
		});
	}
}
function* loadPost(action) {
	try {
		const result = yield call(fetchPost);
		// yield delay(1000);
		yield put({
			type: LOAD_POST_SUCCESS,
			data: result.data,
			// data: generateDummyPost(10),
		});
	} catch (err) {
		yield put({
			type: LOAD_POST_FAILURE,
			data: err,
		});
	}
}
function* likePost(action) {
	try {
		const result = yield call(likePostAPI, action.data);
		yield put({
			type: LIKE_POST_SUCCESS,
			data: result.data,
		});
	} catch (err) {
		console.error(err);
		yield put({
			type: LIKE_POST_FAILURE,
			data: err.response.message,
		});
	}
}
function* unlikePost(action) {
	try {
		const result = yield call(unlikePostAPI, action.data);
		yield put({
			type: UNLIKE_POST_SUCCESS,
			data: result.data,
		});
	} catch (err) {
		yield put({
			type: UNLIKE_POST_FAILURE,
			data: err.response.message,
		});
	}
}
function* uploadImages(action) {
	try {
		const result = yield call(uploadImagesAPI, action.data);
		yield put({
			type: UPLOAD_IMAGES_SUCCESS,
			data: result.data,
		});
	} catch (err) {
		console.error(err);
		yield put({
			type: UPLOAD_IMAGES_FAILURE,
			data: err.response.message,
		});
	}
}
function* watchAddPost() {
	// console.log('saga post');
	yield takeLatest(ADD_POST_REQUEST, addPost);
}
function* watchAddComment() {
	yield takeLatest(ADD_COMMENT_REQUEST, addComment);
}

function* watchRemovePost() {
	// console.log('watching...');
	yield takeLatest(REMOVE_POST_REQUEST, removePost);
}

function* watchLoadPost() {
	yield takeLatest(LOAD_POST_REQUEST, loadPost);
}
function* watchLikePost() {
	yield takeLatest(LIKE_POST_REQUEST, likePost);
}
function* watchUnlikePost() {
	yield takeLatest(UNLIKE_POST_REQUEST, unlikePost);
}
function* watchUploadImages() {
	yield takeLatest(UPLOAD_IMAGES_REQUEST, uploadImages);
}
export default function* postSaga() {
	yield all([
		fork(watchAddPost),
		fork(watchAddComment),
		fork(watchRemovePost),
		fork(watchLoadPost),
		fork(watchLikePost),
		fork(watchUnlikePost),
		fork(watchUploadImages),
	]);
}
