import {
	LOG_IN_REQUEST,
	LOG_IN_SUCCESS,
	LOG_IN_FAILURE,
	LOG_OUT_REQUEST,
	LOG_OUT_SUCCESS,
	LOG_OUT_FAILURE,
} from '../actions/index.js';
import { bindActionCreators } from 'redux';

export const initialState = {
	logInLoading: false,
	logInDone: false,
	logInError: false,
	logOutLoading: false,
	logOutDone: false,
	logOutError: null,
	signUpLoading: false,
	signUpDone: false,
	signUpError: null,
	me: null,
	signupData: null,
	loginData: null,
};

export const loginRequestAction = data => {
	return {
		type: LOG_IN_REQUEST,
		data,
	};
};
export const logoutRequestAction = () => {
	return {
		type: LOG_OUT_REQUEST,
	};
};

const reducer = (state = initialState, action) => {
	switch (action.type) {
		case LOG_IN_REQUEST:
			console.log('reducer');
			return {
				...state,
				logInLoading: true,
				logInDone: false,
				me: action.data,
			};
		case LOG_IN_SUCCESS:
			return {
				...state,
				logInLoading: false,
				logInDone: true,
				me: { ...action.data, nickname: 'pius' },
			};
		case LOG_IN_FAILURE:
			return {
				...state,
				logInLoading: false,
				logInDone: false,
				me: null,
			};

		case LOG_OUT_REQUEST:
			return {
				...state,
				logOutLoading: true,
				logInDone: false,
				me: null,
			};
		case LOG_OUT_SUCCESS:
			return {
				logOutLoading: false,
				logInDone: false,
				me: null,
			};
		case LOG_OUT_FAILURE:
			return {
				logOutLoading: false,
			};
		default:
			return state;
	}
};

export default reducer;
