import { HYDRATE } from 'next-redux-wrapper';
const initialState = {
	user: {
		isLoggedIn: false,
		user: null,
		signupData: null,
		loginData: null,
	},
	post: {
		mainPosts: [],
	},
};

const rootReducer = (state = initialState, action) => {
	switch (action.type) {
		case HYDRATE:
			console.log('HYDRATE', HYDRATE);
			return {
				...state,
				...action.payload,
			};

		default:
			return state;
	}
};

export default rootReducer;
