import { call, put, delay, takeLatest, all, fork } from 'redux-saga/effects';
import {
	LOG_IN_REQUEST,
	LOG_IN_SUCCESS,
	LOG_IN_FAILURE,
	LOG_OUT_REQUEST,
	LOG_OUT_SUCCESS,
	LOG_OUT_FAILURE,
	SIGN_UP_REQUEST,
	SIGN_UP_SUCCESS,
	SIGN_UP_FAILURE,
} from '../actions';
function* login(action) {
	try {
		// const result = yield call();
		yield delay(1000);
		yield put({
			type: LOG_IN_SUCCESS,
			data: action.data,
		});
	} catch (error) {
		yield put({
			type: LOG_IN_FAILURE,
			data: error.response.message,
		});
	}
}
function* logout() {
	try {
		// yield call();
		yield delay(1000);
		yield put({
			type: LOG_OUT_SUCCESS,
		});
	} catch {
		yield put({
			type: LOG_OUT_FAILURE,
		});
	}
}
function* signup() {
	try {
		// yield call();
		yield delay(1000);
		yield put({
			type: SIGN_UP_SUCCESS,
		});
	} catch {
		yield put({
			type: SIGN_UP_FAILURE,
		});
	}
}

function* watchLogin() {
	console.log('saga');
	yield takeLatest(LOG_IN_REQUEST, login);
}

function* watchLogout() {
	yield takeLatest(LOG_OUT_REQUEST, logout);
}
function* watchSignup() {
	yield takeLatest(LOG_OUT_REQUEST, signup);
}

export default function* userSaga() {
	yield all([fork(watchLogin), fork(watchLogout), fork(watchSignup)]);
}
