import {
	LOG_IN_REQUEST,
	LOG_IN_SUCCESS,
	LOG_IN_FAILURE,
	LOG_OUT_REQUEST,
	LOG_OUT_SUCCESS,
	LOG_OUT_FAILURE,
	SIGN_UP_REQUEST,
	SIGN_UP_SUCCESS,
	SIGN_UP_FAILURE,
	CHANGE_NICKNAME_REQUEST,
	CHANGE_NICKNAME_SUCCESS,
	CHANGE_NICKNAME_FAILURE,
	ADD_POST_TO_ME,
} from '../actions/index.js';

export const initialState = {
	logInLoading: false,
	logInDone: false,
	logInError: null,
	logOutLoading: false,
	logOutDone: false,
	logOutError: null,
	signUpLoading: false,
	signUpDone: false,
	signUpError: null,
	changeNicknameLoading: false,
	changeNicknameDone: false,
	changeNicknameError: null,
	me: null,
	signupData: null,
	loginData: null,
};

const dummyUser = data => ({
	...data,
	nickname: 'pius',
	id: 1,
	Posts: [],
	Followings: [
		{
			nickname: 'loopy',
		},
		{
			nickname: 'dopa',
		},
		{
			nickname: 'apdo',
		},
	],
	Followers: [
		{
			nickname: 'loopy',
		},
		{
			nickname: 'dopa',
		},
		{
			nickname: 'apdo',
		},
	],
});
export const loginRequestAction = data => {
	return {
		type: LOG_IN_REQUEST,
		data,
	};
};
export const logoutRequestAction = () => {
	return {
		type: LOG_OUT_REQUEST,
	};
};

const reducer = (state = initialState, action) => {
	switch (action.type) {
		case LOG_IN_REQUEST:
			console.log('reducer');
			return {
				...state,
				logInLoading: true,
				logInDone: false,
				// me: action.data,
			};
		case LOG_IN_SUCCESS:
			return {
				...state,
				logInLoading: false,
				logInDone: true,
				me: dummyUser(action.data),
			};
		case LOG_IN_FAILURE:
			return {
				...state,
				logInLoading: false,
				logInDone: false,
				me: null,
			};

		case LOG_OUT_REQUEST:
			return {
				...state,
				logOutLoading: true,
				logInDone: false,
				me: null,
			};
		case LOG_OUT_SUCCESS:
			return {
				...state,
				logOutLoading: false,
				logInDone: false,
				me: null,
			};
		case LOG_OUT_FAILURE:
			return {
				...state,
				logOutLoading: false,
			};
		case SIGN_UP_REQUEST:
			return {
				...state,
				signUpLoading: true,
				signUpDone: false,
			};
		case SIGN_UP_SUCCESS:
			return {
				...state,
				signUpLoading: false,
				signUpDone: true,
			};
		case SIGN_UP_FAILURE:
			return {
				...state,
				signUpError: true,
			};
		case CHANGE_NICKNAME_REQUEST:
			return {
				...state,
				changeNicknameLoading: true,
				changeNicknameDone: false,
				changeNicknameError: null,
			};
		case CHANGE_NICKNAME_SUCCESS:
			return {
				...state,
				changeNicknameLoading: false,
				changeNicknameDone: true,
			};
		case CHANGE_NICKNAME_FAILURE:
			return {
				...state,
				changeNicknameLoading: false,
				changeNicknameError: action.error,
			};
		case ADD_POST_TO_ME: {
			const newPost = {
				...action.data,
			};
			return {
				...state,
				Post: [action.data, ...state.Posts],
			};
		}
		default:
			return state;
	}
};

export default reducer;
