export const initialState = {
	mainPosts: [
		{
			id: 1,
			User: {
				id: 1,
				nickname: 'pius',
			},
			content: '#Hello#react made by #pius ',
			Images: [
				{
					src:
						'https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcRL13vWu8GOWHAFISKo9v5oBEU75C9udknN2w&usqp=CAU',
				},
				{
					src:
						'https://i.pinimg.com/736x/0b/2f/8a/0b2f8a51314ab1ebe0505aee843a33b1.jpg',
				},
				{
					src:
						'https://pbs.twimg.com/profile_images/1256251357763235842/zY8KO59p_400x400.jpg',
				},
			],
			Comments: [
				{
					User: {
						nickname: 'aksfbs',
					},
					content: '안녕하세요',
				},
			],
		},
	],
	imagePaths: [],
	postAdded: false,
};

const dummyPost = {
	id: 2,
	User: {
		id: 1,
		nickname: 'pius',
	},
	content: 'Hello react??',
	Image: [],
	Comments: [],
};
const ADD_POST = 'ADD_POST';
export const addPost = () => {
	return {
		type: ADD_POST,
	};
};
const reducer = (state = initialState, action) => {
	switch (action.type) {
		case ADD_POST:
			return {
				...state,
				mainPosts: [dummyPost, ...state.mainPosts],
				postAdded: true,
			};
		default:
			return state;
	}
};

export default reducer;
