import React from 'react';
import propTypes from 'prop-types';
import styled from 'styled-components';

const HalfImage = styled.img`
	height: 100%;
	width: 100%;
	/* display: inline; */
`;
const UpperImage = styled.img`
	width: 100%;
	height: 50%;
`;
const LowerImage = styled.div`
	width: 100%;
  height: 50%;
  background-image: {prop=> prop.image};
`;
const ImageContainer = styled.div`
	display: flex;
`;
const LeftImageContainer = styled.div`
	flex-basis: 65%;
`;
const RightImageContainer = styled.div`
	flex-basis: 35%;
	display: flex;
	flex-direction: column;
`;
const PostImages = ({ images }) => {
	if (images.length === 1) {
		return (
			<>
				<img src="" alt="" />
			</>
		);
	}
	if (images.length === 2) {
		return (
			<>
				<HalfImage src={images[0].src}></HalfImage>
				<HalfImage src={images[1].src}></HalfImage>
			</>
		);
	}

	return (
		<>
			<ImageContainer>
				<LeftImageContainer>
					<HalfImage src={images[0].src} alt="" />
				</LeftImageContainer>
				<RightImageContainer>
					<UpperImage src={images[1].src}></UpperImage>
					<LowerImage src={images[2].src}></LowerImage>
				</RightImageContainer>
			</ImageContainer>
		</>
	);
};

PostImages.propTypes = {
	images: propTypes.array,
};

export default PostImages;
