import {
	LOG_IN_REQUEST,
	LOG_IN_SUCCESS,
	LOG_IN_FAILURE,
	LOG_OUT_REQUEST,
	LOG_OUT_SUCCESS,
	LOG_OUT_FAILURE,
	SIGN_UP_REQUEST,
	SIGN_UP_SUCCESS,
	SIGN_UP_FAILURE,
} from '../actions/index.js';
import { bindActionCreators } from 'redux';

export const initialState = {
	logInLoading: false,
	logInDone: false,
	logInError: null,
	logOutLoading: false,
	logOutDone: false,
	logOutError: null,
	signUpLoading: false,
	signUpDone: false,
	signUpError: null,
	me: null,
	signupData: null,
	loginData: null,
};

const dummyUser = data => ({
	...data,
	nickname: 'pius',
	id: 1,
	Posts: [],
	Followings: [],
	Followers: [],
});
export const loginRequestAction = data => {
	return {
		type: LOG_IN_REQUEST,
		data,
	};
};
export const logoutRequestAction = () => {
	return {
		type: LOG_OUT_REQUEST,
	};
};

const reducer = (state = initialState, action) => {
	switch (action.type) {
		case LOG_IN_REQUEST:
			console.log('reducer');
			return {
				...state,
				logInLoading: true,
				logInDone: false,
				// me: action.data,
			};
		case LOG_IN_SUCCESS:
			return {
				...state,
				logInLoading: false,
				logInDone: true,
				me: dummyUser(action.data),
			};
		case LOG_IN_FAILURE:
			return {
				...state,
				logInLoading: false,
				logInDone: false,
				me: null,
			};

		case LOG_OUT_REQUEST:
			return {
				...state,
				logOutLoading: true,
				logInDone: false,
				me: null,
			};
		case LOG_OUT_SUCCESS:
			return {
				logOutLoading: false,
				logInDone: false,
				me: null,
			};
		case LOG_OUT_FAILURE:
			return {
				logOutLoading: false,
			};
		case SIGN_UP_REQUEST:
			return {
				...state,
				signUpLoading: true,
				signUpDone: false,
				me: null,
			};
		case SIGN_UP_SUCCESS:
			return {
				signUpLoading: false,
				signUpDone: true,
				me: null,
			};
		case SIGN_UP_FAILURE:
			return {
				signUpError: true,
			};
		default:
			return state;
	}
};

export default reducer;
