import produce from 'immer';
import {
	LOG_IN_REQUEST,
	LOG_IN_SUCCESS,
	LOG_IN_FAILURE,
	LOG_OUT_REQUEST,
	LOG_OUT_SUCCESS,
	LOG_OUT_FAILURE,
	SIGN_UP_REQUEST,
	SIGN_UP_SUCCESS,
	SIGN_UP_FAILURE,
	CHANGE_NICKNAME_REQUEST,
	CHANGE_NICKNAME_SUCCESS,
	CHANGE_NICKNAME_FAILURE,
	ADD_POST_TO_ME,
	REMOVE_POST_OF_MINE,
} from '../actions/index.js';

export const initialState = {
	logInLoading: false,
	logInDone: false,
	logInError: null,
	logOutLoading: false,
	logOutDone: false,
	logOutError: null,
	signUpLoading: false,
	signUpDone: false,
	signUpError: null,
	changeNicknameLoading: false,
	changeNicknameDone: false,
	changeNicknameError: null,
	me: null,
	signupData: null,
	loginData: null,
};

const dummyUser = data => ({
	...data,
	nickname: 'pius',
	id: 1,
	Posts: [],
	Followings: [
		{
			nickname: 'loopy',
		},
		{
			nickname: 'dopa',
		},
		{
			nickname: 'apdo',
		},
	],
	Followers: [
		{
			nickname: 'loopy',
		},
		{
			nickname: 'dopa',
		},
		{
			nickname: 'apdo',
		},
	],
});
export const loginRequestAction = data => {
	return {
		type: LOG_IN_REQUEST,
		data,
	};
};
export const logoutRequestAction = () => {
	return {
		type: LOG_OUT_REQUEST,
	};
};

const reducer = (state = initialState, action) => {
	return produce(state, draftState => {
		switch (action.type) {
			case LOG_IN_REQUEST:
				draftState.logInLoading = true;
				draftState.logInDone = false;
				draftState.loginError = null;
				break;
			case LOG_IN_SUCCESS:
				draftState.logInLoading = false;
				draftState.logInDone = true;
				draftState.me = dummyUser(action.data);
				break;
			case LOG_IN_FAILURE:
				draftState.logInLoading = false;
				draftState.logInDone = false;
				draftState.me = null;
				break;
			case LOG_OUT_REQUEST:
				draftState.logOutLoading = true;
				draftState.logInDone = false;
				draftState.me = null;
				break;
			case LOG_OUT_SUCCESS:
				draftState.logOutLoading = false;
				draftState.logInDone = false;
				draftState.me = null;
				break;
			case LOG_OUT_FAILURE:
				draftState.logOutLoading = false;
				break;
			case SIGN_UP_REQUEST:
				draftState.signUpLoading = true;
				draftState.signUpDone = false;
				break;
			case SIGN_UP_SUCCESS:
				draftState.signUpLoading = false;
				draftState.signUpDone = true;
				break;
			case SIGN_UP_FAILURE:
				draftState.signUpError = true;
				break;
			case CHANGE_NICKNAME_REQUEST:
				draftState.changeNicknameLoading = true;
				draftState.changeNicknameDone = false;
				draftState.changeNicknameError = null;
				break;
			case CHANGE_NICKNAME_SUCCESS:
				draftState.changeNicknameLoading = false;
				draftState.changeNicknameDone = true;
				break;
			case CHANGE_NICKNAME_FAILURE:
				draftState.changeNicknameLoading = false;
				draftState.changeNicknameError = action.error;
				break;
			case ADD_POST_TO_ME: {
				const newPost = {
					id: action.data,
				};
				draftState.me.Posts.unshift(newPost);
				break;
			}
			case REMOVE_POST_OF_MINE: {
				draftState.me.Posts = draftState.me.Posts.filter(
					item => item.id !== action.data,
				);
				break;
			}
			default:
				break;
		}
	});
};

export default reducer;
