import axios from 'axios';

const instance = axios.create({
	baseURL: 'http://localhost:8080/',
});

const loginUser = data => {
	return instance.post('user/login', data);
};
const registerUser = data => {
	return instance.post('user', data);
	// data = {email, password, nickname}
};

const logoutUser = () => {
	return instance.post('user/logout');
};

// 게시글 업로드
// data = {conetent: text}
// postform.js
// Cors 문제로 인해서 백엔드로 cookie를 보내지 않는 문제를 3번째 인자에
// 옵션을 주어서 해결
const uploadPost = data => {
	return instance.post('post', data, { withCredentials: true });
};

// 댓글 업로드
// data = {
// 	content: comment,
// 	postId: post.id,
// 	userId: id,
// }
// commentForm.js
const uploadComment = data => {
	return instance.post(`post/${data.postId}/comment`, data, {
		withCredentials: true,
	});
};

export { registerUser, loginUser, logoutUser, uploadPost, uploadComment };
