import React, { useCallback, useEffect, useRef } from 'react';
import styled from 'styled-components';
import { useDispatch, useSelector } from 'react-redux';
import { addPostRequest } from '../reducer/post';
import useInput from '../hooks/useInput';
import { UPLOAD_IMAGES_REQUEST } from '../actions/index';
const Form = styled.form`
	margin: 20px 0;
	background-color: #fff;
`;
const TextArea = styled.textarea`
	display: block;
	width: 100%;
`;
const ButtonWrapper = styled.div`
	display: flex;
	justify-content: space-between;
	align-items: center;
	height: 20px;
	margin: 10px 0;
`;

const ImageUploadButton = styled.button`
	padding: 10px 20px;
	display: inline-block;
	text-align: center;
`;
const SubmitButton = styled.button`
	display: inline-block;
	text-align: center;
	margin-left: auto;
	background-color: #5f0080;
	color: white;
	padding: 10px 20px;
`;
const Wrapper = styled.div`
	display: inline-block;
	width: 100%;
`;
const PreviewImage = styled.img`
	width: 200px;
`;
const PostForm = () => {
	const dispatch = useDispatch();
	const [text, onChangeText, setText] = useInput('');
	const { addPostDone } = useSelector(state => state.post);
	const imageInput = useRef(null);
	useEffect(() => {
		if (addPostDone) {
			setText('');
		}
	}, [setText, addPostDone]);
	const handleSubmit = useCallback(
		e => {
			e.preventDefault();
			if (text === '') {
				return;
			}
			dispatch(addPostRequest({ content: text }));
		},
		[text, dispatch],
	);
	const onChangeImages = useCallback(e => {
		console.log('images', e.target.files);
		const imageFormData = new FormData();
		[].forEach.call(e.target.files, file => {
			imageFormData.append('image', file);
		});
		dispatch({
			type: UPLOAD_IMAGES_REQUEST,
			data: imageFormData,
		});
	});
	const openDialog = useCallback(() => {
		imageInput.current.click();
	});
	return (
		<Form onSubmit={handleSubmit}>
			<TextArea type="textarea" row="4" value={text} onChange={onChangeText} />
			<Wrapper>
				<ButtonWrapper>
					<input
						id="inputButton"
						name="image"
						ref={imageInput}
						type="file"
						onChange={onChangeImages}
						hidden
					/>
					<ImageUploadButton onClick={openDialog}>
						이미지 업로드
					</ImageUploadButton>
					<SubmitButton type="submit">생성</SubmitButton>
				</ButtonWrapper>
			</Wrapper>
			<div>
				<PreviewImage></PreviewImage>
			</div>
		</Form>
	);
};

export default PostForm;
