import {
	ADD_POST_REQUEST,
	ADD_POST_SUCCESS,
	ADD_POST_FAILURE,
	ADD_COMMENT_REQUEST,
	ADD_COMMENT_SUCCESS,
	ADD_COMMENT_FAILURE,
	ADD_POST_TO_ME,
	REMOVE_POST_OF_MINE,
} from '../actions';
import shortid from 'shortid';
import { useDispatch } from 'react-redux';

export const initialState = {
	mainPosts: [
		{
			id: 1,
			User: {
				id: 1,
				nickname: 'pius',
			},
			content: '#Hello#react made by #pius ',
			Images: [
				{
					id: shortid.generate(),
					src:
						'https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcRL13vWu8GOWHAFISKo9v5oBEU75C9udknN2w&usqp=CAU',
				},
				{
					id: shortid.generate(),
					src:
						'https://i.pinimg.com/736x/0b/2f/8a/0b2f8a51314ab1ebe0505aee843a33b1.jpg',
				},
				{
					id: shortid.generate(),
					src:
						'https://pbs.twimg.com/profile_images/1256251357763235842/zY8KO59p_400x400.jpg',
				},
			],
			Comments: [
				{
					id: shortid.generate(),
					User: {
						id: shortid.generate(),
						nickname: 'aksfbs',
					},
					content: '안녕하세요',
				},
			],
		},
	],
	imagePaths: [],
	addPostLoading: false,
	addPostDone: false,
	addPostError: null,
	addCommentLoading: false,
	addCommentDone: false,
	addCommentError: null,
};

const dummyPost = text => ({
	id: shortid.generate(),
	User: {
		id: 1,
		nickname: 'pius',
	},
	content: text,
	Image: [],
	Comments: [],
});

const dummyComment = content => ({
	id: shortid.generate(),
	User: {
		nickname: 'aksfbs',
	},
	content: content,
});
export const addPostRequest = data => {
	return {
		type: ADD_POST_REQUEST,
		data: data,
	};
};

export const addCommentRequest = data => {
	return {
		type: ADD_COMMENT_REQUEST,
		data: data,
	};
};

const reducer = (state = initialState, action) => {
	const dispatch = useDispatch();

	switch (action.type) {
		// POST
		case ADD_POST_REQUEST:
			console.log('add post request, reducer');
			return {
				...state,
				addPostLoading: true,
				addPostDone: false,
			};
		case ADD_POST_SUCCESS:
			console.log('add post success, reducer');
			dispatch({
				type: ADD_POST_TO_ME,
				data: action.data,
			});
			return {
				...state,
				mainPosts: [dummyPost(action.data), ...state.mainPosts],
				addPostLoading: false,
				addPostDone: true,
			};
		case ADD_POST_FAILURE:
			return {
				...state,
				addPostError: action.error,
			};
		// Comment
		case ADD_COMMENT_REQUEST:
			return {
				...state,
				addCommentLoading: true,
				addCommentDone: false,
				addCommentError: null,
			};
		case ADD_COMMENT_SUCCESS: {
			//action.data.content, postId, userId
			// comment form에서 comment를 추가할때,
			// store에 있는 mainPosts를 찾아서 추가해준다.
			const idx = state.mainPosts.findIndex(
				item => item.id === action.data.postId,
			);
			const post = { ...state.mainPosts[idx] };
			post.Comments = [dummyComment(action.data.content), ...post.Comments];
			const mainPosts = [...state.mainPosts];
			mainPosts[idx] = post;
			return {
				...state,
				mainPosts,
				addCommentLoading: false,
				addCommentDone: true,
			};
		}
		case ADD_COMMENT_FAILURE:
			return {
				...state,
				addCommentError: action.error,
			};
		default:
			return state;
	}
};

export default reducer;
